function refreshData() {
    var e = moment.utc();
    for (rollingChronoShopDate = moment.utc("2017-03-10T17:00:00Z"); e.isAfter(rollingChronoShopDate); ) rollingChronoShopDate.add(14, "days");
    var o = rollingChronoShopDate.local().format("MMMM Do") + " at " + rollingChronoShopDate.format("hA");
    document.getElementById("cameojokes").innerText = "That's " + o + " your time, buddy.";
}

function getTimeRemaining() {
    var e = moment.utc();
    e.isAfter(rollingChronoShopDate) && refreshData();
    var o = moment.utc(rollingChronoShopDate).diff(e), t = moment.duration(o);
    return t.days() + " days, " + t.hours() + " hours, " + t.minutes() + " minutes, " + t.seconds() + " seconds";
}

function updateTimer() {
    var e = getTimeRemaining();
    document.getElementById("dusty").innerText = e;
}

var rollingChronoShopDate;

refreshData(), updateTimer(), setInterval(updateTimer, 1e3);