function sOrNah(e, t) {
    document.getElementById("time").innerHTML += 1 == e ? "1 " + t + " " : e + " " + t + "s ";
}

function showRemaining() {
    var e = new Date().getTime(), t = testDay - e;
    if (t < 0) return clearInterval(timer), void (document.getElementById("time").innerHTML = "EXPIRED!");
    var n = Math.floor(t / 864e5), a = Math.floor(t % 864e5 / 36e5), o = Math.floor(t % 36e5 / 6e4), i = Math.floor(t % 6e4 / 1e3);
    document.getElementById("time").innerHTML = "", sOrNah(n, "day"), sOrNah(a, "hour"), 
    sOrNah(o, "min"), sOrNah(i, "second");
}

var now = new Date(), ref = new Date("Fri Sept 22 2017 17:00:00 GMT+0100 (GMT Daylight Time)"), testDay = ref;

window.onload = function() {
    for (;testDay < now; ) testDay = new Date(testDay.setDate(testDay.getDate() + 14));
    window.setInterval(function() {
        showRemaining();
    }, 1e3);
    showRemaining();
};